import{_ as t,E as i,j as n,k as d,a5 as s,A as l}from"./chunks/framework.3Z9w-w8R.js";const g=JSON.parse('{"title":"Damage","description":"/damage command explained.","frontmatter":{"title":"Damage","description":"/damage command explained.","category":"Commands","mentions":["BedrockCommands","cda94581","QuazChick"]},"headers":[{"level":2,"title":"Introduction","slug":"introduction","link":"#introduction","children":[]},{"level":2,"title":"Syntax","slug":"syntax","link":"#syntax","children":[]},{"level":2,"title":"Arguments","slug":"arguments","link":"#arguments","children":[]},{"level":2,"title":"Variables","slug":"variables","link":"#variables","children":[]},{"level":2,"title":"Examples","slug":"examples","link":"#examples","children":[]},{"level":2,"title":"List of Damage Causes","slug":"list-of-damage-causes","link":"#list-of-damage-causes","children":[]}],"relativePath":"commands/damage.md","filePath":"commands/damage.md"}'),o={name:"commands/damage.md"};function c(r,a,p,h,m,u){const e=i("CodeHeader");return d(),n("div",null,[a[0]||(a[0]=s('<h2 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h2><p><a href="https://bedrockcommands.org/" target="_blank" rel="noreferrer">Sourced by the Bedrock Commands Community (BCC) Discord</a></p><p>Introduced in Minecraft Release <code class="shiki"><span>1.18.10</span></code>, the /damage command deals precise damage to specified entities. With this change, the clunky methods like using <code class="shiki"><span>/effect</span></code> command to damage entities are rendered obsolete, making maps and other creations more powerful.</p><h2 id="syntax" tabindex="-1">Syntax <a class="header-anchor" href="#syntax" aria-label="Permalink to &quot;Syntax&quot;">​</a></h2><ul><li>There are two ways the damage command can be used: <ul><li><code class="shiki"><span>/damage &lt;Target&gt; &lt;Amount&gt; [Cause]</span></code></li><li><code class="shiki"><span>/damage &lt;Target&gt; &lt;Amount&gt; &lt;Cause&gt; entity &lt;Damager&gt;</span></code></li></ul></li></ul><h2 id="arguments" tabindex="-1">Arguments <a class="header-anchor" href="#arguments" aria-label="Permalink to &quot;Arguments&quot;">​</a></h2><ul><li>Phrases not contained in angle &lt;&gt; or square [] brackets instruct you to type it as-is.</li><li>Phrases contained within brackets are variables, these need to be replaced: <ul><li><strong><code class="shiki"><span>&lt;&gt;</span></code></strong> Angle brackets mean the variable is required.</li><li><strong><code class="shiki"><span>[]</span></code></strong> Square brackets mean the variable is optional.</li></ul></li></ul><h2 id="variables" tabindex="-1">Variables <a class="header-anchor" href="#variables" aria-label="Permalink to &quot;Variables&quot;">​</a></h2><ul><li><p><strong><code class="shiki"><span>Target</span></code></strong> This is your typical entity selector, such as <code class="shiki"><span>@s</span></code> , <code class="shiki"><span>@e</span></code> , or <code class="shiki"><span>&quot;cda94581&quot;</span></code> . Multiple entities may be selected at a time to deal the damage to multiple targets.</p></li><li><p><strong><code class="shiki"><span>Amount</span></code></strong> This is a whole number, which specifies the amount of damage to deal to the targets. The minimum value is <code class="shiki"><span>0</span></code> and the maximum value is <code class="shiki"><span>2147483647</span></code>, or the signed 32-bit integer limit.</p></li><li><p><strong><code class="shiki"><span>Cause</span></code></strong> This specifies the &quot;reason&quot; the damage was dealt. This cause will appear in death messages (<code class="shiki"><span>X hit the ground too hard for cause: fall</span></code>) be used in damage calculation with armor (<code class="shiki"><span>the value dealt in Amount may be different depending on the worn armor</span></code>), and used in a large variety of other things, such as in Behavior Pack/Add-ons. A full list of all the damage causes can be found <a href="#list-of-damage-causes">below</a>.</p></li><li><p><strong><code class="shiki"><span>Damager</span></code></strong> If Cause was something to do with entities <code class="shiki"><span>(such as entity_attack)</span></code>, this specifies where the damage came from <code class="shiki"><span>(the entity that dealt the attack)</span></code>. This is limited to only 1 target. An error will be thrown if multiple targets are found from the selector.</p></li></ul><blockquote><p>Note: the <code class="shiki"><span>&lt;Cause&gt; entity &lt;Damager&gt;</span></code> is only required when the Cause has to do with another entity <code class="shiki"><span>(entity_attack)</span></code>. Otherwise, follow the first syntax.</p></blockquote><h2 id="examples" tabindex="-1">Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;Examples&quot;">​</a></h2>',11)),l(e),a[1]||(a[1]=s(`<div class="language-yaml vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes dark-plus light-plus vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#6A9955;--shiki-light:#008000;"># Deal 4 damage to all players</span></span>
<span class="line"><span style="--shiki-dark:#CE9178;--shiki-light:#0000FF;">/damage @a 4</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-dark:#6A9955;--shiki-light:#008000;"># Deal 3 &#39;fire&#39; damage to all entities of type &#39;sheep&#39;</span></span>
<span class="line"><span style="--shiki-dark:#CE9178;--shiki-light:#0000FF;">/damage @e[type=sheep] 3 fire</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-dark:#6A9955;--shiki-light:#008000;"># Deal 40 &#39;entity attack&#39; damage from a random player to all entities of type &#39;sheep&#39;</span></span>
<span class="line"><span style="--shiki-dark:#CE9178;--shiki-light:#0000FF;">/damage @e[type=sheep] 40 entity_attack entity @r</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h2 id="list-of-damage-causes" tabindex="-1">List of Damage Causes <a class="header-anchor" href="#list-of-damage-causes" aria-label="Permalink to &quot;List of Damage Causes&quot;">​</a></h2><p>Listed below are all the &#39;cause&#39; values that can be used in the <code class="shiki"><span>/damage</span></code> command:</p><table tabindex="0"><thead><tr><th>Damage Cause</th></tr></thead><tbody><tr><td><code class="shiki"><span>all</span></code></td></tr><tr><td><code class="shiki"><span>anvil</span></code></td></tr><tr><td><code class="shiki"><span>block_explosion</span></code></td></tr><tr><td><code class="shiki"><span>campfire</span></code></td></tr><tr><td><code class="shiki"><span>charging</span></code></td></tr><tr><td><code class="shiki"><span>contact</span></code></td></tr><tr><td><code class="shiki"><span>drowning</span></code></td></tr><tr><td><code class="shiki"><span>entity_attack</span></code></td></tr><tr><td><code class="shiki"><span>entity_explosion</span></code></td></tr><tr><td><code class="shiki"><span>fall</span></code></td></tr><tr><td><code class="shiki"><span>falling_block</span></code></td></tr><tr><td><code class="shiki"><span>fire</span></code></td></tr><tr><td><code class="shiki"><span>fire_tick</span></code></td></tr><tr><td><code class="shiki"><span>fireworks</span></code></td></tr><tr><td><code class="shiki"><span>fly_into_wall</span></code></td></tr><tr><td><code class="shiki"><span>freezing</span></code></td></tr><tr><td><code class="shiki"><span>lava</span></code></td></tr><tr><td><code class="shiki"><span>lightning</span></code></td></tr><tr><td><code class="shiki"><span>magic</span></code></td></tr><tr><td><code class="shiki"><span>magma</span></code></td></tr><tr><td><code class="shiki"><span>none</span></code></td></tr><tr><td><code class="shiki"><span>override</span></code></td></tr><tr><td><code class="shiki"><span>piston</span></code></td></tr><tr><td><code class="shiki"><span>projectile</span></code></td></tr><tr><td><code class="shiki"><span>self_destruct</span></code></td></tr><tr><td><code class="shiki"><span>ram_attack</span></code></td></tr><tr><td><code class="shiki"><span>sonic_boom</span></code></td></tr><tr><td><code class="shiki"><span>soul_campfire</span></code></td></tr><tr><td><code class="shiki"><span>stalactite</span></code></td></tr><tr><td><code class="shiki"><span>stalagmite</span></code></td></tr><tr><td><code class="shiki"><span>starve</span></code></td></tr><tr><td><code class="shiki"><span>suffocation</span></code></td></tr><tr><td><code class="shiki"><span>temperature</span></code></td></tr><tr><td><code class="shiki"><span>thorns</span></code></td></tr><tr><td><code class="shiki"><span>void</span></code></td></tr><tr><td><code class="shiki"><span>wither</span></code></td></tr></tbody></table>`,4))])}const b=t(o,[["render",c]]);export{g as __pageData,b as default};
