import{_ as i}from"./chunks/command-block.repeat.Bnoxkihw.js";import{_ as m}from"./chunks/4.CFjFOGEC.js";import{_ as d,E as t,j as p,k as h,a5 as k,A as s,I as l,l as o,J as n}from"./chunks/framework.3Z9w-w8R.js";const u="/assets/images/commands/intro-to-command-blocks/command-block-ui.png",g="/assets/images/commands/intro-to-command-blocks/command-block.impulse.png",c="/assets/images/commands/intro-to-command-blocks/command-block.chain.png",b="/assets/images/commands/intro-to-command-blocks/command-block.impulse-conditional.png",_="/assets/images/commands/intro-to-command-blocks/command-block.chain-conditional.png",f="/assets/images/commands/intro-to-command-blocks/command-block.repeat-conditional.png",q="/assets/images/commands/intro-to-command-blocks/game-tick.png",v="/assets/images/commands/intro-to-command-blocks/command-block-hover-note.png",x="/assets/images/commands/intro-to-command-blocks/command-block-paste-button.png",P="/assets/images/commands/intro-to-command-blocks/command-block-placement.correct.png",C="/assets/images/commands/intro-to-command-blocks/command-block-placement.incorrect.png",$=JSON.parse('{"title":"命令方块简介","description":"MCBE命令方块介绍。","frontmatter":{"title":"命令方块简介","category":"General","mentions":["BedrockCommands","zheaEvyline","jordanparki7"],"nav_order":1,"description":"MCBE命令方块介绍。"},"headers":[{"level":2,"title":"简介","slug":"简介","link":"#简介","children":[]},{"level":2,"title":"获取","slug":"获取","link":"#获取","children":[]},{"level":2,"title":"命令方块UI","slug":"命令方块ui","link":"#命令方块ui","children":[]},{"level":2,"title":"命令方块类型","slug":"命令方块类型","link":"#命令方块类型","children":[]},{"level":2,"title":"命令方块条件","slug":"命令方块条件","link":"#命令方块条件","children":[]},{"level":2,"title":"命令方块红石状态","slug":"命令方块红石状态","link":"#命令方块红石状态","children":[]},{"level":2,"title":"命令方块刻度延迟","slug":"命令方块刻度延迟","link":"#命令方块刻度延迟","children":[]},{"level":2,"title":"命令方块悬停说明","slug":"命令方块悬停说明","link":"#命令方块悬停说明","children":[]},{"level":2,"title":"粘贴按钮","slug":"粘贴按钮","link":"#粘贴按钮","children":[]},{"level":2,"title":"命令方块输出","slug":"命令方块输出","link":"#命令方块输出","children":[{"level":3,"title":"在聊天中禁用命令消息","slug":"在聊天中禁用命令消息","link":"#在聊天中禁用命令消息","children":[]}]},{"level":2,"title":"命令方块放置","slug":"命令方块放置","link":"#命令方块放置","children":[]},{"level":2,"title":"命令方块故障排除","slug":"命令方块故障排除","link":"#命令方块故障排除","children":[]},{"level":2,"title":"您学到的内容","slug":"您学到的内容","link":"#您学到的内容","children":[]},{"level":2,"title":"接下来是什么？","slug":"接下来是什么","link":"#接下来是什么","children":[]}],"relativePath":"commands/intro-to-command-blocks.md","filePath":"commands/intro-to-command-blocks.md"}'),B={name:"commands/intro-to-command-blocks.md"};function I(T,a,E,N,U,V){const e=t("Card"),r=t("CardGrid");return h(),p("div",null,[a[2]||(a[2]=k('<h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p><a href="https://bedrockcommands.org/" target="_blank" rel="noreferrer">由Bedrock Commands Community (BCC) Discord提供来源</a></p><p>命令方块是Minecraft中的特殊方块。您在聊天中输入的相同命令（作弊码）可以使用命令方块自动运行，并且可以重复使用而无需再次全部输入。</p><p>只有拥有创造模式中操作员权限的玩家才能放置或销毁它们。</p><h2 id="获取" tabindex="-1">获取 <a class="header-anchor" href="#获取" aria-label="Permalink to &quot;获取&quot;">​</a></h2><ol><li>打开您的世界设置。</li><li>在作弊码下，将&quot;激活作弊码&quot;设置切换为开启。</li><li>在聊天中运行<code class="shiki"><span>/give @s command_block</span></code>。</li></ol><h2 id="命令方块ui" tabindex="-1">命令方块UI <a class="header-anchor" href="#命令方块ui" aria-label="Permalink to &quot;命令方块UI&quot;">​</a></h2><p><img src="'+u+'" alt="命令方块Ui"></p><h2 id="命令方块类型" tabindex="-1">命令方块类型 <a class="header-anchor" href="#命令方块类型" aria-label="Permalink to &quot;命令方块类型&quot;">​</a></h2><p><img src="'+g+'" alt="脉冲命令方块"> <strong>脉冲</strong> 每次供电时<strong>一次</strong>运行命令。</p><p><img src="'+c+'" alt="连锁命令方块"> <strong>连锁</strong> 按顺序运行命令，即仅在连接的上一个命令方块运行后才运行。</p><p><img src="'+i+'" alt="循环命令方块"> <strong>循环</strong> 每个游戏刻度运行命令。大约每秒有20个刻度。可以应用延迟来调整命令执行的频率，如下<a href="/commands/intro-to-command-blocks#command-block-tick-delay">所述</a>。</p><h2 id="命令方块条件" tabindex="-1">命令方块条件 <a class="header-anchor" href="#命令方块条件" aria-label="Permalink to &quot;命令方块条件&quot;">​</a></h2><p><strong>条件</strong>命令方块仅在连接的上一个命令方块有成功输出(<code class="shiki"><span>true</span></code>)时运行命令。</p><blockquote><p>条件命令方块状态通过命令方块箭头纹理上的小凹痕显示，如下所示：</p><ul><li><img src="'+b+'" alt="脉冲条件命令方块"> 脉冲条件命令方块</li><li><img src="'+_+'" alt="连锁条件命令方块"> 连锁条件命令方块</li><li><img src="'+f+'" alt="循环条件命令方块"> 循环条件命令方块</li></ul></blockquote><p><strong>无条件</strong>命令方块无论结果如何都会运行命令。无论连接的上一个命令方块是否有成功输出(<code class="shiki"><span>true</span></code>)或失败(<code class="shiki"><span>false</span></code>)，命令方块仍会运行命令。</p><h2 id="命令方块红石状态" tabindex="-1">命令方块红石状态 <a class="header-anchor" href="#命令方块红石状态" aria-label="Permalink to &quot;命令方块红石状态&quot;">​</a></h2><p><strong>需要红石</strong>命令方块只能通过红石能量激活。使用按钮、拉杆、红石火把等。</p><p><strong>始终激活</strong>命令方块在关闭命令方块UI时立即激活。</p><h2 id="命令方块刻度延迟" tabindex="-1">命令方块刻度延迟 <a class="header-anchor" href="#命令方块刻度延迟" aria-label="Permalink to &quot;命令方块刻度延迟&quot;">​</a></h2><p>在此选项中，您可以指定命令方块运行命令前应有多少延迟。</p><p>刻度指的是Minecraft游戏刻度。<strong>刻度</strong>只是游戏中时间的测量单位。现实生活中1秒大约等于Minecraft中的20个游戏刻度。</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><img src="'+q+'" alt="命令在游戏刻度中运行的大致位置"></p></div><h2 id="命令方块悬停说明" tabindex="-1">命令方块悬停说明 <a class="header-anchor" href="#命令方块悬停说明" aria-label="Permalink to &quot;命令方块悬停说明&quot;">​</a></h2><p>此选项允许您在命令方块上放置悬停文本。在处理多个命令方块链时，这对于给予简短名称以便轻松识别很有用。</p><p>当运行命令时，如果启用了gamerule <code class="shiki"><span>commandblockoutput</span></code>，悬停说明将与输出一起显示在聊天中。 <img src="'+v+'" alt="命令方块悬停说明"></p><h2 id="粘贴按钮" tabindex="-1">粘贴按钮 <a class="header-anchor" href="#粘贴按钮" aria-label="Permalink to &quot;粘贴按钮&quot;">​</a></h2><p><img src="'+x+'" alt="命令方块粘贴按钮"></p><p>粘贴按钮允许您将先前复制的坐标粘贴到&#39;命令输入&#39;框中。</p><h2 id="命令方块输出" tabindex="-1">命令方块输出 <a class="header-anchor" href="#命令方块输出" aria-label="Permalink to &quot;命令方块输出&quot;">​</a></h2><ul><li><p>在命令方块UI中切换&#39;上一个输出&#39;按钮以查看命令输出和方块详情。</p></li><li><p>您在完整命令前输入的<code class="shiki"><span>/</span></code>在命令方块中不是必需的，但这样做不会导致任何错误。</p></li><li><p>红石比较器可以读取命令方块输出。如果输出成功，它将返回1到15个红石信号，具体取决于输出强度。</p></li><li><p>您可以在聊天中运行它来检查命令方块输出是否为<code class="shiki"><span>true</span></code>/<code class="shiki"><span>false</span></code>。红色文本输出或<code class="shiki"><span>0</span></code>输出表示语法错误或失败(<code class="shiki"><span>false</span></code>)输出。白色文本输出表示命令已成功运行。</p></li><li><p>您还可以通过检查动作是否执行来判断命令是否为<code class="shiki"><span>true</span></code>/<code class="shiki"><span>false</span></code>。</p></li></ul><h3 id="在聊天中禁用命令消息" tabindex="-1">在聊天中禁用命令消息 <a class="header-anchor" href="#在聊天中禁用命令消息" aria-label="Permalink to &quot;在聊天中禁用命令消息&quot;">​</a></h3><p>在聊天中运行：</p><ul><li><code class="shiki"><span>/gamerule commandblockoutput false</span></code>以在聊天中禁用命令方块消息。</li><li><code class="shiki"><span>/gamerule sendcommandfeedback false</span></code>以禁用从聊天中输入的命令的反馈。</li></ul><h2 id="命令方块放置" tabindex="-1">命令方块放置 <a class="header-anchor" href="#命令方块放置" aria-label="Permalink to &quot;命令方块放置&quot;">​</a></h2><p>在链中放置命令方块（排列为按顺序工作）用于任何系统时，请确保连续的命令方块从上一个命令方块箭头的头部连接/开始。</p><p>可以从侧面的命令方块纹理观察箭头/朝向。</p><p><strong>✅ 正确放置</strong><img src="'+P+'" alt="正确命令方块放置"></p><p><strong>❌ 错误放置</strong><img src="'+C+'" alt="错误命令方块放置"></p><h2 id="命令方块故障排除" tabindex="-1">命令方块故障排除 <a class="header-anchor" href="#命令方块故障排除" aria-label="Permalink to &quot;命令方块故障排除&quot;">​</a></h2><ul><li><p>在世界设置中，在<strong>作弊码</strong>下，确保命令方块未被禁用。</p></li><li><p>确保gamerule <code class="shiki"><span>maxcommandchainlength</span></code>未设置为0</p></li><li><p>确保没有任何不需要的红石能量干扰命令方块。它可能来自红石粉、拉杆、红石火把等。</p></li><li><p>尝试在&#39;始终激活&#39;和&#39;需要红石&#39;之间切换。</p></li><li><p>仔细检查方块类型、条件和命令语法。供电后，再次检查&#39;上一个输出&#39;。</p></li><li><p>就像红石一样，命令方块也必须在加载的区块内才能工作。当玩家不在附近时，您可以使用tick区域使其保持加载。有关更多信息，请参阅<a href="https://learn.microsoft.com/en-us/minecraft/creator/documents/tickingareacommand" target="_blank" rel="noreferrer">/tickingarea</a>命令文档。</p></li></ul><p>如果没有效果，只需打破并更换该命令方块即可。</p><h2 id="您学到的内容" tabindex="-1">您学到的内容 <a class="header-anchor" href="#您学到的内容" aria-label="Permalink to &quot;您学到的内容&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">您学到的内容：</p><ul><li>如何在游戏中获得命令方块。</li><li>不同类型的命令方块的行为及其外观。</li><li>不同命令方块选项是什么（包括条件、状态和延迟。）</li><li>命令方块如何通过红石和聊天消息输出数据。</li><li>如何正确放置命令方块链。</li><li>如何解决&#39;命令方块不工作&#39;</li></ul></div><blockquote><p>设置codeblock/mcfunction命令方块系统时，请始终记住，第一个命令方块将是<img src="'+i+'" alt="循环命令方块"> <strong><code class="shiki"><span>无条件始终激活</span></code></strong>，其余命令方块将是<img src="'+c+'" alt="连锁命令方块"> <strong><code class="shiki"><span>无条件始终激活</span></code></strong>。除非另有说明，否则全部设置为0刻度延迟。</p><p><img src="'+m+'" alt="四个命令方块链"></p></blockquote><h2 id="接下来是什么" tabindex="-1">接下来是什么？ <a class="header-anchor" href="#接下来是什么" aria-label="Permalink to &quot;接下来是什么？&quot;">​</a></h2>',46)),s(r,null,{default:l(()=>[s(e,{title:"基础系统",image:"/assets/images/icons/command_block.png"},{default:l(()=>a[0]||(a[0]=[o("p",null,[n("为了将您学到的知识付诸实践，尝试制作这个简单的**"),o("a",{href:"/commands/on-first-join"},"玩家首次加入"),o("strong",null,"系统，或"),o("a",{href:"/commands/entity-counter"},"实体计数器"),n("**系统。")],-1)])),_:1}),s(e,{title:"理解选择器",link:"/commands/selectors",image:"/assets/images/icons/parrot.png"},{default:l(()=>a[1]||(a[1]=[o("p",null,"学习在命令中使用目标选择器以及如何过滤它们。",-1)])),_:1})]),_:1})])}const A=d(B,[["render",I]]);export{$ as __pageData,A as default};
