import{_ as o,E as l,j as r,k as p,a5 as t,A as s,I as i,J as n}from"./chunks/framework.3Z9w-w8R.js";const _=JSON.parse('{"title":"Intro to Text & Localization","description":"Minecraft is a game with fully localized text in languages all over the world.","frontmatter":{"title":"Intro to Text & Localization","description":"Minecraft is a game with fully localized text in languages all over the world.","category":"General","nav_order":1,"mentions":["ThijsHankelMC","SirLich","aexer0e","MedicalJewel105","Lufurrius","Fabrimat","TheDoctor15","Hatchibombotar","ChibiMango","SmokeyStack","Sprunkles","QuazChick"]},"headers":[{"level":2,"title":"Language Files","slug":"language-files","link":"#language-files","children":[{"level":3,"title":"File Location","slug":"file-location","link":"#file-location","children":[]},{"level":3,"title":"Format","slug":"format","link":"#format","children":[]},{"level":3,"title":"Usage","slug":"usage","link":"#usage","children":[]}]},{"level":2,"title":"Localization","slug":"localization","link":"#localization","children":[{"level":3,"title":"Custom Languages","slug":"custom-languages","link":"#custom-languages","children":[]},{"level":3,"title":"Tools","slug":"tools","link":"#tools","children":[]},{"level":3,"title":"Vanilla Languages","slug":"vanilla-languages","link":"#vanilla-languages","children":[]}]}],"relativePath":"text/text-intro.md","filePath":"text/text-intro.md"}'),h={name:"text/text-intro.md"};function c(u,a,g,k,m,b){const d=l("FolderView"),e=l("CodeHeader");return p(),r("div",null,[a[3]||(a[3]=t('<p>Minecraft is a game with fully localized text in languages all over the world. To achieve this, Minecraft employs a system where internal <strong>translation keys</strong> are assigned values on a per-language basis. Minecraft will generate translation keys for custom entities, items, and blocks, and it is up to us to assign them a localized name in our resource pack.</p><h2 id="language-files" tabindex="-1">Language Files <a class="header-anchor" href="#language-files" aria-label="Permalink to &quot;Language Files&quot;">​</a></h2><h3 id="file-location" tabindex="-1">File Location <a class="header-anchor" href="#file-location" aria-label="Permalink to &quot;File Location&quot;">​</a></h3><p>Language files typically go within the resource pack in the &quot;texts&quot; folder as files with the <code class="shiki"><span>.lang</span></code> file extension. These files can be placed in the behavior pack, but the only translatable text it can change is the pack manifest&#39;s name and description.</p>',4)),s(d,{paths:["RP/texts/en_US.lang","RP/texts/languages.json","RP/manifest.json"]}),a[4]||(a[4]=t(`<p>Minecraft supports 29 languages currently, as described in <a href="/text/text-intro#vanilla-languages">§ Vanilla Languages</a>.</p><h3 id="format" tabindex="-1">Format <a class="header-anchor" href="#format" aria-label="Permalink to &quot;Format&quot;">​</a></h3><p>The format for a language file is rather straightforward. Translations are supplied as key-value pairs separated by an equals sign (<code class="shiki"><span>=</span></code>), the key being a translation key and the value being a string. Values cannot contain newline characters.</p><div class="language-lang vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">lang</span><pre class="shiki shiki-themes dark-plus light-plus vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#9CDCFE;--shiki-light:#E50000;">wiki.example_translation.line_1</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">=</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">The first line!</span></span>
<span class="line"><span style="--shiki-dark:#9CDCFE;--shiki-light:#E50000;">wiki.example_translation.line_2</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">=</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">Some more information following the first line.</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>Comments may be added with two pound signs (<code class="shiki"><span>##</span></code>), either as line comments or in-line comments. All text after the pound signs are a comment until the next line.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Trailing spaces are not trimmed for in-line comments. If you want to indent a comment, use the Tab character.</p></div><div class="language-lang vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">lang</span><pre class="shiki shiki-themes dark-plus light-plus vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#6A9955;--shiki-light:#008000;">## Translator note: I thought this would be funny to put here.</span></span>
<span class="line"><span style="--shiki-dark:#9CDCFE;--shiki-light:#E50000;">item.flint_and_steel.name</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">=</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">Flint and Steve</span><span style="--shiki-dark:#6A9955;--shiki-light:#008000;">	##[sic]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>A translation can contain substitutions in place of text. Substitutions can either be ordered (<code class="shiki"><span>%1</span></code>, <code class="shiki"><span>%2</span></code>, etc.) or not ordered (<code class="shiki"><span>%s</span></code>). Vanilla translations have their values filled in by the game, while players can manually set the substitutions&#39; values with commands that use the raw JSON text format, like with <a href="/text/rawtext"><code class="shiki"><span>/tellraw</span></code></a>.</p><div class="language-lang vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">lang</span><pre class="shiki shiki-themes dark-plus light-plus vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#9CDCFE;--shiki-light:#E50000;">commands.op.success</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">=</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">Opped: </span><span style="--shiki-dark:#D7BA7D;--shiki-light:#EE0000;">%s</span></span>
<span class="line"><span style="--shiki-dark:#9CDCFE;--shiki-light:#E50000;">immersive_reader.book_page_header</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">=</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">Page </span><span style="--shiki-dark:#D7BA7D;--shiki-light:#EE0000;">%1</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;"> of </span><span style="--shiki-dark:#D7BA7D;--shiki-light:#EE0000;">%2</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to &quot;Usage&quot;">​</a></h3><p>Localization can be done just about anywhere text can be used, including (but not limited to):</p><ul><li>Pack name and description</li><li>Entity, item, or block names</li><li>Pages in a book</li><li>Lines on a sign</li><li><code class="shiki"><span>/tellraw</span></code> and <code class="shiki"><span>/titleraw</span></code> commands</li><li>Text in dialogue</li></ul><p>Some text cannot be translated however, such as for an item renamed in an anvil.</p><h2 id="localization" tabindex="-1">Localization <a class="header-anchor" href="#localization" aria-label="Permalink to &quot;Localization&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>It is good practice create a copy of your language file for each major language your pack supports. For example, to support full English one should create both an <code class="shiki"><span>en_US.lang</span></code> and an <code class="shiki"><span>en_GB.lang</span></code> file, to cover English in both the United States and Great Britain countries, respectively.</p></div><p>When editing language files one must also add a <code class="shiki"><span>languages.json</span></code> file in the <code class="shiki"><span>texts</span></code> folder containing an array with each of the languages you plan to change. This lets Minecraft know that it should apply localization for these languages.</p>`,16)),s(e,null,{default:i(()=>a[0]||(a[0]=[n("RP/texts/languages.json")])),_:1}),a[5]||(a[5]=t('<div class="language-json vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">json</span><pre class="shiki shiki-themes dark-plus light-plus vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">[</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;en_US&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">, </span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;en_GB&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">, </span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;fr_FR&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="custom-languages" tabindex="-1">Custom Languages <a class="header-anchor" href="#custom-languages" aria-label="Permalink to &quot;Custom Languages&quot;">​</a></h3><p>With a global resource pack, custom languages may be introduced through the <code class="shiki"><span>languages.json</span></code> and <code class="shiki"><span>language_names.json</span></code> files. Once the pack is applied globally the language can be changed in the &quot;Language&quot; tab of the in-game settings.</p><p>For the following examples, lets assume that we have 2 fully functional language files, one named <code class="shiki"><span>xx_XX.lang</span></code>, and another named <code class="shiki"><span>yy_YY.lang</span></code>.</p>',4)),s(e,null,{default:i(()=>a[1]||(a[1]=[n("RP/texts/languages.json")])),_:1}),a[6]||(a[6]=t('<div class="language-json vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">json</span><pre class="shiki shiki-themes dark-plus light-plus vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">[</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;xx_XX&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">, </span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;yy_YY&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><code class="shiki"><span>language_names.json</span></code> is an array as well, but this time to define the names to display for the languages.</p>',2)),s(e,null,{default:i(()=>a[2]||(a[2]=[n("RP/texts/language_names.json")])),_:1}),a[7]||(a[7]=t(`<div class="language-json vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">json</span><pre class="shiki shiki-themes dark-plus light-plus vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">[</span></span>
<span class="line"><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">    [</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;xx_XX&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">, </span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;New Language (Custom Language #1)&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">],</span></span>
<span class="line"><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">    [</span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;yy_YY&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">, </span><span style="--shiki-dark:#CE9178;--shiki-light:#A31515;">&quot;Wiki-Speak (Custom Language #2)&quot;</span><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">]</span></span>
<span class="line"><span style="--shiki-dark:#D4D4D4;--shiki-light:#000000;">]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Whenever using a custom language, make sure to switch away from the language before you disable the resource pack that it is a part of, otherwise Minecraft will crash.</p></div><h3 id="tools" tabindex="-1">Tools <a class="header-anchor" href="#tools" aria-label="Permalink to &quot;Tools&quot;">​</a></h3><p>If Microsoft are localizing your .lang file there are specific technical requirements that your .lang must follow.</p><ul><li>Ensure that you have &lt;tab&gt;# before comments (<strong>not</strong> spaces).</li><li>Ensure line breaks are the Windows style (CR+LF), not Unix style.</li><li>Must not contain duplicate keys.</li><li>Strings must be commented to ease translation.</li></ul><p>You can use the free browser-based <a href="https://langutil.bedrockexplorer.com" target="_blank" rel="noreferrer">LangUtil tool</a> to assist with this.</p><h3 id="vanilla-languages" tabindex="-1">Vanilla Languages <a class="header-anchor" href="#vanilla-languages" aria-label="Permalink to &quot;Vanilla Languages&quot;">​</a></h3><p>The following is a table of the 29 languages Minecraft supports by default.</p><table tabindex="0"><thead><tr><th>Language ID</th><th>Language Name</th><th>Country</th></tr></thead><tbody><tr><td>id_ID</td><td>Indonesian</td><td>Indonesia</td></tr><tr><td>da_DK</td><td>Danish</td><td>Denmark</td></tr><tr><td>de_DE</td><td>German</td><td>Germany</td></tr><tr><td>en_GB</td><td>English</td><td>Great Britain</td></tr><tr><td>en_US</td><td>English</td><td>North America</td></tr><tr><td>es_ES</td><td>Spanish</td><td>Spain</td></tr><tr><td>es_MX</td><td>Mexican Spanish</td><td>Mexico</td></tr><tr><td>fr_CA</td><td>Canadian French</td><td>Canada</td></tr><tr><td>fr_FR</td><td>French</td><td>France</td></tr><tr><td>it_IT</td><td>Italian</td><td>Italy</td></tr><tr><td>hu_HU</td><td>Hungarian</td><td>Hungary</td></tr><tr><td>nl_NL</td><td>Dutch</td><td>Netherlands</td></tr><tr><td>nb_NO</td><td>Bokmål</td><td>Norway</td></tr><tr><td>pl_PL</td><td>Polish</td><td>Poland</td></tr><tr><td>pt_BR</td><td>Brazilian Portuguese</td><td>Brazil</td></tr><tr><td>pt_PT</td><td>Portuguese</td><td>Portugal</td></tr><tr><td>sk_SK</td><td>Slovak</td><td>Slovakia</td></tr><tr><td>fi_FI</td><td>Finnish</td><td>Finland</td></tr><tr><td>sv_SE</td><td>Swedish</td><td>Sweden</td></tr><tr><td>tr_TR</td><td>Turkish</td><td>Turkey</td></tr><tr><td>cs_CZ</td><td>Czech</td><td>Czech Republic</td></tr><tr><td>el_GR</td><td>Greek</td><td>Greece</td></tr><tr><td>bg_BG</td><td>Bulgarian</td><td>Bulgaria</td></tr><tr><td>ru_RU</td><td>Russian</td><td>Russia</td></tr><tr><td>uk_UA</td><td>Ukrainian</td><td>Ukraine</td></tr><tr><td>ja_JP</td><td>Japanese</td><td>Japan</td></tr><tr><td>zh_CN</td><td>Chinese (Simplified)</td><td>China</td></tr><tr><td>zh_TW</td><td>Chinese (Traditional)</td><td>Taiwan</td></tr><tr><td>ko_KR</td><td>Korean</td><td>Korea</td></tr></tbody></table>`,9))])}const f=o(h,[["render",c]]);export{_ as __pageData,f as default};
